import React, {useState} from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function Login(props){
    
    const navigate = useNavigate();

    const [userData, setUserData] = useState(
        {
            username: "",
            password: ""
        }
    );

    const [showWarning, setShowWarning] = useState(false);

    function handleOnChange(event){
        const target = event.target;
        setUserData( (prev)=>{
            return (
                {
                    ...prev,
                    [target.name] : target.value
                }
            )
        } );
    }

    async function handleOnClick(event){
        const targetName = event.target.name;
        if(targetName === "login"){
            const url = props.SERVER_API + "/login";
            const data = {username: userData.username , password: userData.password};
            const response = await axios.post(url, data , {headers: {'content-type': 'application/x-www-form-urlencoded'}});
            if(response.data === "error"){
                setShowWarning(true);
            }else{
                navigate("/todos" , {
                    state: {
                        username: userData.username,
                        userTodos: response.data.todos,
                        userID: response.data.userID
                    }
                });
            }
        }else{
            navigate("/home");
        }
    }

    return (

        <main className="form-signin w-100 m-auto">
            <form>
                <h1 class="h3 mb-3 fw-normal">Please sign in</h1>
                
                <div className="form-floating">
                    <input type="email" class="form-control" id="floatingInput" placeholder="name@example.com" fdprocessedid="wxumr" />
                    <label for="floatingInput">Email address</label>
                </div>
                
                <div class="form-floating">
                    <input type="password" class="form-control" id="floatingPassword" placeholder="Password" fdprocessedid="ejlxra" />
                    <label for="floatingPassword">Password</label>
                </div>
                
                <div class="form-check text-start my-3">
                    <input class="form-check-input" type="checkbox" value="remember-me" id="flexCheckDefault" />
                    <label class="form-check-label" for="flexCheckDefault">
                        Remember me
                    </label>
                </div>
                
                <button class="btn btn-primary w-100 py-2" type="submit" fdprocessedid="9k5b0o">Sign in</button>
                <p class="mt-5 mb-3 text-body-secondary">© 2017–2023</p>
            </form>
        </main>


        // <div className="main">
        //     <h1>Login</h1>
        //     <input onChange={handleOnChange} type="text" placeholder="Username" name="username"/>
        //     <input onChange={handleOnChange} type="password" placeholder="Password" name="password"/>
        //     <button onClick={handleOnClick} name="login">Login</button>
        //     {showWarning? <p style={{color: "red"}}>Incorrect username or password</p> : <br/>}
        //     <button onClick={handleOnClick} name="home">Home page</button>
        // </div>
    )
}

export default Login;
