import React , {useState} from "react";
import { useLocation,useNavigate } from "react-router-dom";
import axios from "axios";

function Todos(props){

    const navigate = useNavigate();
    const {state} = useLocation();
    const [todos , setTodos] = useState(state.userTodos);
    const [showBox , setShowBox] = useState(false);
    const [newTodo , setNewTodo] = useState("");

    async function handleOnClick(event){
        const target = event.target;
        const baseURL = props.SERVER_API;
        
        if(target.name === "deleteTodo"){
            const data = {params : { userID: state.userID , todoID: target.id }}
            const response = await axios.delete(baseURL + "/todos", data);
            setTodos(response.data);
        }

        else if(target.name === "updateTodo"){
            const data = { userID: state.userID , todoID: target.id }
            const response = await axios.patch(baseURL + "/todos", data , {headers: {'content-type': 'application/x-www-form-urlencoded'}});
            setTodos(response.data);
        }
        
        else if(target.name === "logout"){
            navigate("/home");
        }
        
        else if(target.name === "showBox"){
            setShowBox(true);
        }
        
        else if(target.name === "exitNewTodo"){
            setShowBox(false);
            setNewTodo("");
        }
        
        else if(target.name === "addNewTodo"){
            if(newTodo === ""){
                return;
            }
            const data = {userID: state.userID , text: newTodo};
            const response = await axios.post(baseURL + "/todos/new" , data , {headers: {'content-type': 'application/x-www-form-urlencoded'}} );
            setTodos(response.data);
            setNewTodo("");
        }
    }

    function handleOnChange(event){
        const value = event.target.value;
        setNewTodo(value);
    }

    return(
        <div className="main">
            <h1>Welcome {state.username} !</h1>
            <ul class="list-group">
            {todos.map( (todo)=>{
                return (
                    <div>
                        <li key={todo._id} class="list-group-item">
                            <input class="form-check-input me-1" type="checkbox" value="" id={todo._id}/>
                            <label class="form-check-label" for={todo._id}>{todo.text}</label>
                        </li>

                        
                    </div>
                )
            } )}
            </ul>
            <button onClick={handleOnClick} name="logout" className="btn btn-danger mx-2">Logout</button>
            <button onClick={handleOnClick} name="showBox" className="btn btn-success mx-2">Add New Todo</button>
            {showBox?   <div>
                            <br/>
                            <input type="text" onChange={handleOnChange} value={newTodo}/>
                            <button onClick={handleOnClick} name="exitNewTodo">Cancel</button>
                            <button onClick={handleOnClick} name="addNewTodo">Add</button>
                        </div> : <br/>}
        </div>
    )
}

export default Todos;
