import React, {useState} from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function Register(props){
    
    const navigate = useNavigate();

    const [userData, setUserData] = useState(
        {
            username: "",
            password: ""
        }
    );

    const [showWarning , setShowWarning] = useState(false);

    function handleOnChange(event){
        const target = event.target;
        setUserData((prev)=>{
            return (
                {
                    ...prev,
                    [target.name]: target.value
                }
            )
        });
    }

    async function handleOnClick(event){
        const buttonName = event.target.name;
            if(buttonName === "register"){
            const url = props.SERVER_API + "/register";
            const data = {username: userData.username , password: userData.password};
            const response = await axios.post(url, data, {headers: {'content-type': 'application/x-www-form-urlencoded'}});
            if(response.data === "exists"){
                setShowWarning(true);
            }else{
            }
        }else{
            navigate("/home");
        }
    }

    return (
        <div>
            <h1>Register</h1>
            <input onChange={handleOnChange} type="text" placeholder="Username" name="username"/>
            <input onChange={handleOnChange} type="password" placeholder="Password" name="password"/>
            <button onClick={handleOnClick} name="register">Register</button>
            { showWarning ? <p style={ {color: "red"} }>This user already exists, please log in</p> : <></> }
            <button onClick={handleOnClick} name="homePage">Home page</button>
        </div>
    )
}

export default Register;
